const darkSwitch=document.getElementById("switchDark");
function initTheme(){
    const e=null!==localStorage.getItem("switchDark")&&"dark"===localStorage.getItem("switchDark");
    darkSwitch.checked=e,e?document.body.setAttribute("data-theme","dark"):document.body.removeAttribute("data-theme")
}
function resetTheme(){
    darkSwitch.checked?(document.body.setAttribute("data-theme","dark"),localStorage.setItem("switchDark","dark")):(document.body.removeAttribute("data-theme"),localStorage.removeItem("switchDark"))
}
window.addEventListener("load",()=>{
    darkSwitch&&(initTheme(),darkSwitch.addEventListener("change",()=>{
        resetTheme()}
        ))
    });